
<#
	.SYNOPSIS
	Version: 1.6
	Date: 11.08.2022
	Copyright (c) 2022 THEBOSSMAGNUS
	.DESCRIPTION
	A simple and open source tool for install runtime.
	.NOTES
	Set execution policy if it fail to run:
		Set-ExecutionPolicy -ExecutionPolicy Bypass -Scope Process -force
	.NOTES
	Require admin execution
    .NOTES
    For windows 10 & 11. Require Powershell 5(preinstalled in windows 10/11) or 7
    .NOTES
    Warning: Wach the known issues in github release!
	.LINK
	https://github.com/THEBOSSMAGNUS/Runtime_Toolbox
#>
#Requires -RunAsAdministrator
#Requires -Version 5.1

Add-Type -AssemblyName System.Windows.Forms
[System.Windows.Forms.Application]::EnableVisualStyles()


#check if Winget is intalled
if (Test-Path ~\AppData\Local\Microsoft\WindowsApps\winget.exe) {
    Write-Output "Winget is already Installed"}
 else {Write-error "You must download and install Winget from https://apps.microsoft.com/store/detail/9NBLGGH4NNS1"
     Pause
     exit}

 #check if the internet connection is available
 if (!(Test-Connection "www.google.com" -Count 1 -Quiet)) {
     Write-Error "For running Runtime Toolbox you need an internet connection"
     Pause
     exit}
 else{Write-Host "Internet connection is working"}

 #check OS
 If((Get-itempropertyvalue -path "HKLM:\SOFTWARE\Microsoft\Windows NT\CurrentVersion" -name ProductName) -like "Windows 10*") {
     Write-Output "Your OS is supported"}
 else {Write-Error "This program is only for Windows 10 or 11"
     Pause
     exit}


#Gui
    #main form
    $Windows = New-Object system.Windows.Forms.Form
        $Windows.text = "Runtime Toolbox"
        $Windows.ClientSize = "527, 300"
        $Windows.FormBorderStyle = "FixedSingle"

    #icon
        $iconBase64 = ""
        $iconBytes                       = [Convert]::FromBase64String($iconBase64)
        $stream                          = New-Object IO.MemoryStream($iconBytes, 0, $iconBytes.Length)
        $stream.Write($iconBytes, 0, $iconBytes.Length)
        $windows.Icon                    = [System.Drawing.Icon]::FromHandle((New-Object System.Drawing.Bitmap -Argument $stream).GetHIcon())

    #install button
    $button = New-Object System.Windows.Forms.Button
        $Windows.Controls.Add($button)
        $button.TabIndex = 4
        $button.Width = 250
        $button.Height = 50
        $button.Text = "Install"
        $button.Location = New-Object System.Drawing.Point(250,165)
        $button.Font = New-Object System.Drawing.Font('Segoe UI Variable',20)
        $button.add_Click($handler_install_Click)

    #Stust console
    $Console = New-Object System.Windows.Forms.ListBox
        $Windows.Controls.Add($Console)
        $Console.Width = 250
        $Console.Height = 150
        $Console.Location = New-Object System.Drawing.Point(250,13)
        $Console.TabIndex = 3


#checkbox
    #MSVC 2015-2022
    $VC1522 = New-Object System.Windows.Forms.CheckBox
        $Windows.Controls.Add($VC1522)
        $VC1522.Width = 105
        $VC1522.Height = 30
        $VC1522.location = New-Object System.Drawing.Point(27,10)
        $VC1522.Text = "C++ visual 2015-2022"

    #MSVC 2013
    $VC13 = New-Object System.Windows.Forms.CheckBox
        $Windows.Controls.Add($VC13)
        $VC13.Width = 105
        $VC13.Height = 30
        $VC13.location = New-Object System.Drawing.Point(27,50)
        $VC13.Text = "C++ visual 2013"

 #MSVC 2012
     $VC12 = New-Object System.Windows.Forms.CheckBox
         $Windows.Controls.Add($VC12)
         $VC12.Width = 105
         $VC12.Height = 30
         $VC12.location = New-Object System.Drawing.Point(27,90)
         $VC12.Text = "C++ visual 2012"

    #MSVC 2010
    $VC10 = New-Object System.Windows.Forms.CheckBox
        $Windows.Controls.Add($VC10)
        $VC10.Width = 105
        $VC10.Height = 30
        $VC10.location = New-Object System.Drawing.Point(27,130)
        $VC10.Text = "C++ visual 2010"

    #MSVC 2008
    $VC8 = New-Object System.Windows.Forms.CheckBox
        $Windows.Controls.Add($VC8)
        $VC8.Width = 105
        $VC8.Height = 30
        $VC8.location = New-Object System.Drawing.Point(27,170)
        $VC8.Text = "C++ visual 2008"

    #MSVC 2005
    $VC5 = New-Object System.Windows.Forms.CheckBox
        $Windows.Controls.Add($VC5)
        $VC5.Width = 105
        $VC5.Height = 30
        $VC5.location = New-Object System.Drawing.Point(27,210)
        $VC5.Text = "C++ visual 2005"

    #Java
    $Java = New-Object System.Windows.Forms.CheckBox
        $Windows.Controls.Add($Java)
        $Windows.Controls.Add($Java)
        $Java.Width = 105
        $Java.Height = 30
        $Java.location = New-Object System.Drawing.Point(145,10)
        $Java.Text = "Java  8  runtime"

    #.net 3.5
    $NET35 = New-Object System.Windows.Forms.CheckBox
        $Windows.Controls.Add($NET35)
        $NET35.Width = 105
        $NET35.Height = 30
        $NET35.location = New-Object System.Drawing.Point(145,50)
        $NET35.Text = ".NET Framework 3.5"

    #.net 6
    $NET6 = New-Object System.Windows.Forms.CheckBox
        $Windows.Controls.Add($NET6)
        $NET6.Width = 105
        $NET6.Height = 30
        $NET6.location = New-Object System.Drawing.Point(145,90)
        $NET6.Text = ".NET 6 desktop runtime"

    #XNA
    $XNA = New-Object System.Windows.Forms.CheckBox
        $Windows.Controls.Add($XNA)
        $XNA.Width = 105
        $XNA.Height = 30
        $XNA.location = New-Object System.Drawing.Point(145,130)
        $XNA.Text = "XNA Framework"

    #PowerShell
    $PS = New-Object System.Windows.Forms.CheckBox
        $Windows.Controls.Add($PS)
        $PS.Width = 105
        $PS.Height = 30
        $PS.location = New-Object System.Drawing.Point(145,170)
        $PS.Text = "PowerShell 7"

    #DirectX 9
    $DX9 = New-Object System.Windows.Forms.CheckBox
        $Windows.Controls.Add($DX9)
        $DX9.Width = 105
        $DX9.Height = 30
        $DX9.location = New-Object System.Drawing.Point(145,210)
        $DX9.Text = "Directx 9"


$Windows.ShowDialog() #Show the Form


$handler_install_Click= {
    $console.Items.Clear()


    if ($VC1522.Checked)  {Msvc20152022}

    if ($VC13.Checked)    {Msvc2013}

    if ($VC12.Checked)    {Msvc2012}

    if ($VC10.Checked)    {Msvc2010}

    if ($VC8.Checked)     {Msvc2008}

    if ($VC5.Checked)     {Msvc2005}

    if ($Java.Checked)      {Java}

    if ($NET35.Checked)     {Netfx35}

    if ($NET6.Checked)      {Net6}

    if ($XNA.Checked)       {XNA}

    if ($PS.Checked)        {Powershell}

    if ($DX9.Checked)       {Directx9}


    $Console.Items.Add("All done!")
}

function Msvc20152022 {
    winget install --id=Microsoft.VC++2015-2022Redist-x64 -h --accept-source-agreements
    Write-Host "MSVC 2015-2022 x64 installed" -f green
    winget install --id=Microsoft.VC++2015-2022Redist-x86 -h
    Write-Host "MSVC 2015-2022 x86 installed" -f green
    $Console.Items.Add("MSVC 2015-2022 installed")}

    function Msvc2013 {
        winget install --id=Microsoft.VC++2013Redist-x64 -h
        Write-Host "MSVC 2013 x64 installed" -f green
        winget install --id=Microsoft.VC++2013Redist-x86 -h
        Write-Host "MSVC 2013 x86 installed" -f green
        $Console.Items.Add("MSVC 2013 installed")}

    function Msvc2012 {
        winget install --id=Microsoft.VC++2012Redist-x64 -h
        Write-Host "MSVC 2012 x64 installed" -f green
        winget install --id=Microsoft.VC++2012Redist-x86 -h
        Write-Host "MSVC 2012 x86 installed" -f green
        $Console.Items.Add("MSVC 2012 installed")}

    function Msvc2010 {
        winget install --id=Microsoft.VC++2010Redist-x64 -h
        Write-Host "MSVC 2010 x64 installed" -f green
        winget install --id=Microsoft.VC++2010Redist-x86 -h
        Write-Host "MSVC 2010 x86 installed" -f green
        $Console.Items.Add("MSVC 2010 installed")}

    function Msvc2008 {
        winget install --id=Microsoft.VC++2008Redist-x64 -h
        Write-Host "MSVC 2008 x64 installed" -f green
        winget install --id=Microsoft.VC++2008Redist-x86 -h
        Write-Host "MSVC 2008 x86 installed" -f green
        $Console.Items.Add("MSVC 2008 installed")}

    function Msvc2005 {
        winget install --id=Microsoft.VC++2005Redist-x64 -h
        Write-Host "MSVC 2005x64 installed" -f green
        winget install --id=Microsoft.VC++2005Redist-x86 -h
        Write-Host "MSVC 2005x86 installed" -f green
        $Console.Items.Add("MSVC 2005 installed")}

    function Java {
        winget install --id=Oracle.JavaRuntimeEnvironment -h
        Write-Host "Java 8 runtime envirorment installed" -f green
        $Console.Items.Add("Java 8 runtime envirorment installed")}

    function Netfx35 {
        Enable-WindowsOptionalFeature -Online -f eatureName "NetFx3" -All
        $Console.Items.Add("Net Framework 3.5 installed")}
        Write-Host ".Net desktop runtime 6 installed" -f green

    function Net6 {
        winget install --id=Microsoft.DotNet.DesktopRuntime.6 -h
        $Console.Items.Add("Net 6 desktop runtime installed")}
        Write-Host ".Net framework 3.5 installed" -f green

    function XNA {
        winget install --id=Microsoft.XNA.Framework -h
        $Console.Items.Add("Xna Framework installed")}
        write-host "XNA framework installed" -f green

    function PowerShell {
        winget install --id=Microsoft.PowerShell -h
        $Console.Items.Add("Powershell installed")}
        Write-Host "PowerShell installed" -f green

    function Directx9 {
        winget install  --id=Microsoft.DirectX -h
        $Console.Items.Add("Directx 9 installed")}
        write-host "DirectX 9 installed" -f green
